  --shadow-sm: 0px 2px 0px 0px hsl(25 8% 88% / 0.00), 0px 1px 2px -1px hsl(25 8% 88% / 0.00);
  --shadow: 0px 2px 0px 0px hsl(25 8% 88% / 0.00), 0px 1px 2px -1px hsl(25 8% 88% / 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(25 8% 88% / 0.00), 0px 2px 4px -1px hsl(25 8% 88% / 0.00);
