import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";

interface CalendarDay {
  day: number;
  minutes?: number;
  rounds?: string[];
  isToday?: boolean;
}

interface PracticeCalendarProps {
  month: string;
  days: CalendarDay[];
}

export default function PracticeCalendar({ month, days }: PracticeCalendarProps) {
  const roundColors = {
    'NS': 'bg-green-100 text-green-800',
    'SP': 'bg-blue-100 text-blue-800', 
    'TG': 'bg-yellow-100 text-yellow-800',
    'TM': 'bg-purple-100 text-purple-800'
  };

  return (
    <Card data-testid="card-practice-calendar">
      <CardHeader>
        <CardTitle className="text-lg">{month} — minutes & round chips</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="grid grid-cols-7 gap-1 mb-4">
          {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
            <div key={day} className="text-center text-sm font-medium text-muted-foreground p-2">
              {day}
            </div>
          ))}
        </div>

        <div className="grid grid-cols-7 gap-1">
          {days.map((dayData, index) => (
            <div 
              key={index} 
              className={`min-h-16 p-2 border rounded-lg ${
                dayData.isToday ? 'border-primary bg-primary/5' : 'border-border'
              }`}
              data-testid={`calendar-day-${dayData.day}`}
            >
              <div className="text-sm font-medium text-card-foreground mb-1">
                {dayData.day}
              </div>
              {dayData.minutes && (
                <div className="text-xs text-muted-foreground mb-1">
                  {dayData.minutes}m
                </div>
              )}
              <div className="flex flex-wrap gap-1">
                {dayData.rounds?.map((round, i) => (
                  <Badge 
                    key={i} 
                    className={`text-xs px-1 py-0 ${roundColors[round as keyof typeof roundColors]}`}
                    data-testid={`badge-round-${round}`}
                  >
                    {round}
                  </Badge>
                ))}
              </div>
            </div>
          ))}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/ProblemBankManager.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { useState } from "react";

interface Problem {
  id: string;
  type: string;
  prompt: string;
  answer: string;
  tags: string[];
}

interface ProblemBankManagerProps {
  problems: Problem[];
  onAddProblem?: (problem: Partial<Problem>) => void;
}

export default function ProblemBankManager({ problems, onAddProblem }: ProblemBankManagerProps) {
  const [newProblem, setNewProblem] = useState({
    type: '',
    prompt: '',
    answer: '',
    tags: '',
    source: '',
    difficulty: ''
  });

  const handleAddProblem = () => {
    onAddProblem?.({
      ...newProblem,
      tags: newProblem.tags.split(',').map(tag => tag.trim())
    });
    console.log('Adding problem:', newProblem);
    setNewProblem({ type: '', prompt: '', answer: '', tags: '', source: '', difficulty: '' });
  };

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6" data-testid="problem-bank-manager">
      <Card data-testid="card-add-problems">
        <CardHeader>
          <CardTitle>Add new problems</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div>
            <label className="text-sm font-medium text-card-foreground">Type</label>
            <Select onValueChange={(value) => setNewProblem({...newProblem, type: value})}>
              <SelectTrigger data-testid="select-problem-type">
                <SelectValue placeholder="Number Sense / Sprint / Target / Team" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="number-sense">Number Sense</SelectItem>
                <SelectItem value="sprint">Sprint</SelectItem>
                <SelectItem value="target">Target</SelectItem>
                <SelectItem value="team">Team</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div>
            <label className="text-sm font-medium text-card-foreground">Source</label>
            <Input 
              placeholder="Book page / PDF / URL / Manual"
              value={newProblem.source}
              onChange={(e) => setNewProblem({...newProblem, source: e.target.value})}
              data-testid="input-source"
            />
          </div>

          <div>
            <label className="text-sm font-medium text-card-foreground">Difficulty</label>
            <Input 
              placeholder="1-5"
              value={newProblem.difficulty}
              onChange={(e) => setNewProblem({...newProblem, difficulty: e.target.value})}
              data-testid="input-difficulty"
            />
          </div>

          <div>
            <label className="text-sm font-medium text-card-foreground">Topic tags</label>
            <Input 
              placeholder="fractions, ratios, geometry..."
              value={newProblem.tags}
              onChange={(e) => setNewProblem({...newProblem, tags: e.target.value})}
              data-testid="input-tags"
            />
          </div>

          <div className="border rounded-lg p-4 min-h-24 bg-muted/50">
            <p className="text-sm text-muted-foreground mb-2">Drag & drop images/PDFs or paste text</p>
            <Input 
              placeholder="Problem text..."
              value={newProblem.prompt}
              onChange={(e) => setNewProblem({...newProblem, prompt: e.target.value})}
              data-testid="input-problem-text"
            />
          </div>

          <div className="flex gap-2">
            <Button 
              variant="outline" 
              data-testid="button-upload"
              onClick={() => console.log('Upload clicked')}
            >
              Upload
            </Button>
          </div>

          <div className="flex gap-2 pt-2">
            <Button 
              onClick={handleAddProblem}
              disabled={!newProblem.type || !newProblem.prompt}
              data-testid="button-add-problem"
            >
              Add
            </Button>
            <Button 
              variant="outline" 
              data-testid="button-save-draft"
              onClick={() => console.log('Save draft clicked')}
            >
              Save draft
            </Button>
          </div>
        </CardContent>
      </Card>

      <Card data-testid="card-problems-list">
        <CardHeader>
          <CardTitle>Problems</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-3">
            {problems.map((problem) => (
              <div 
                key={problem.id} 
                className="border rounded-lg p-3 hover-elevate cursor-pointer"
                data-testid={`problem-item-${problem.id}`}
                onClick={() => console.log('Problem selected:', problem.id)}
              >
                <div className="flex items-center justify-between mb-2">
                  <div className="flex items-center gap-2">
                    <span className="text-sm font-medium">{problem.id}</span>
                    <Badge variant="outline">{problem.type}</Badge>
                  </div>
                  <span className="text-sm text-muted-foreground">{problem.answer}</span>
                </div>
                <p className="text-sm text-card-foreground truncate">{problem.prompt}</p>
                <div className="flex flex-wrap gap-1 mt-2">
                  {problem.tags.map((tag, i) => (
                    <Badge key={i} variant="secondary" className="text-xs">{tag}</Badge>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/ProblemInterface.t.content:
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { useState } from "react";

interface ProblemInterfaceProps {
  roundType: 'sprint' | 'target' | 'numbersense' | 'team';
  problem: {
    id: string;
    text: string;
    choices?: string[];
    type: 'multiple_choice' | 'numerical';
  };
  onAnswer: (answer: string) => void;
  showHint?: boolean;
  onHint?: () => void;
}

export default function ProblemInterface({ roundType, problem, onAnswer, showHint, onHint }: ProblemInterfaceProps) {
  const [selectedAnswer, setSelectedAnswer] = useState<string>('');
  const [numericalAnswer, setNumericalAnswer] = useState<string>('');

  const handleSubmit = () => {
    const answer = problem.type === 'multiple_choice' ? selectedAnswer : numericalAnswer;
    onAnswer(answer);
    console.log(`Submitted answer: ${answer}`);
  };

  const roundColors = {
    sprint: 'border-l-4 border-l-orange-400',
    target: 'border-l-4 border-l-blue-400',
    numbersense: 'border-l-4 border-l-green-400', 
    team: 'border-l-4 border-l-purple-400'
  };

  return (
    <Card className={`${roundColors[roundType]}`} data-testid="card-problem">
      <CardHeader className="pb-4">
        <div className="flex items-center justify-between">
          <Badge variant="outline" data-testid="badge-round-type">
            {roundType.charAt(0).toUpperCase() + roundType.slice(1)}
          </Badge>
          <div className="flex gap-2">
            {showHint && (
              <Button 
                variant="outline" 
                size="sm" 
                data-testid="button-hint"
                onClick={onHint || (() => console.log('Hint requested'))}
              >
                Hint
              </Button>
            )}
            <Button 
              variant="outline" 
              size="sm" 
              data-testid="button-next"
              onClick={() => console.log('Next problem')}
            >
              Next →
            </Button>
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-6">
        <div className="text-lg text-card-foreground" data-testid="problem-text">
          {problem.text}
        </div>

        {problem.type === 'multiple_choice' ? (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
            {problem.choices?.map((choice, index) => {
              const letter = String.fromCharCode(65 + index);
              return (
                <Button
                  key={index}
                  variant={selectedAnswer === letter ? "default" : "outline"}
                  className="text-left justify-start h-auto p-4 hover-elevate"
                  onClick={() => setSelectedAnswer(letter)}
                  data-testid={`button-choice-${letter}`}
                >
                  <span className="font-semibold mr-2">{letter}.</span>
                  {choice}
                </Button>
              );
            })}
          </div>
        ) : (
          <div className="space-y-4">
            <Input
              type="text"
              placeholder="Answer (simplest form)"
              value={numericalAnswer}
              onChange={(e) => setNumericalAnswer(e.target.value)}
              className="text-lg"
              data-testid="input-numerical-answer"
            />
            {roundType === 'numbersense' && (
              <div className="text-xs text-muted-foreground">
                Estimation → integer only
              </div>
            )}
          </div>
        )}

        <div className="flex justify-end">
          <Button 
            onClick={handleSubmit}
            disabled={problem.type === 'multiple_choice' ? !selectedAnswer : !numericalAnswer}
            data-testid="button-submit-answer"
          >
            Submit Answer
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/RoundCard.tsx.content:
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";

interface RoundCardProps {
  title: string;
  description: string;
  duration: string;
  calcAllowed: boolean;
  color: 'orange' | 'blue' | 'green' | 'purple';
  onClick?: () => void;
}

export default function RoundCard({ title, description, duration, calcAllowed, color, onClick }: RoundCardProps) {
  const colorClasses = {
    orange: 'border-l-4 border-l-orange-400',
    blue: 'border-l-4 border-l-blue-400', 
    green: 'border-l-4 border-l-green-400',
    purple: 'border-l-4 border-l-purple-400'
  };

  const badgeColors = {
    orange: 'bg-orange-100 text-orange-800',
    blue: 'bg-blue-100 text-blue-800',
    green: 'bg-green-100 text-green-800', 
    purple: 'bg-purple-100 text-purple-800'
  };

  return (
    <Card 
      className={`hover-elevate cursor-pointer transition-all ${colorClasses[color]}`}
      onClick={onClick}
      data-testid={`card-round-${title.toLowerCase().replace(' ', '-')}`}
    >
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-3">
          <h3 className="text-xl font-semibold text-card-foreground">{title}</h3>
          <Badge 
            className={`${badgeColors[color]} ${calcAllowed ? '' : 'opacity-70'}`}
            data-testid={`badge-calc-${calcAllowed ? 'ok' : 'no'}`}
          >
            {calcAllowed ? 'Calc OK' : 'No Calc'}
          </Badge>
        </div>
        <p className="text-muted-foreground mb-4">{description}</p>
        <div className="flex items-center justify-between">
          <span className="text-sm font-medium text-card-foreground">{duration}</span>
          <Button size="sm" data-testid={`button-start-${title.toLowerCase().replace(' ', '-')}`}>
            Start Practice
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

